import React, { useEffect, useState } from "react";
import "./Searchbar.css";
import { IoSearchOutline } from "react-icons/io5";
import axios from "axios";

const SearchBar = ({ onSearch }) => {
  // const [userInput, setUserInput] = useState('');
  // const [responseMessage, setResponseMessage] = useState('');

  // const sendDataToFlask = async () => {
  //   const apiUrl = 'http://localhost';

  //   const requestData = {
  //     type: 'custom_type', // You can set a fixed type or make it dynamic as well
  //     message: userInput,
  //   };

  //   try {
  //     const response = await axios.post(apiUrl, requestData);
  //     setResponseMessage(response.data.message);
  //   } catch (error) {
  //     console.error('Error:', error);
  //   }
  // };

  const [userInput, setUserInput] = useState('');
  const [processedData, setProcessedData] = useState('');

  const sendDataToFlask = async () => {
    const apiUrl = 'http://localhost:5000/flask/process_data';

    const requestData = {
      message: userInput,
    };

    try {
      const response = await axios.post(apiUrl, requestData);
      setProcessedData(response.data.message);
    } catch (error) {
      console.error('Error:', error);
    }
  };


  return (
    <>
     <div>
      <label>
        Enter Message:
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
        />
      </label>
      <button onClick={sendDataToFlask}>Send POST Request</button>
      <p>Processed Data from Flask: {processedData}</p>
    </div>
    </>
  );
};

export default SearchBar;
