import React, { useEffect, useState } from "react";
import "./Searchbar.css";
import { IoSearchOutline } from "react-icons/io5";
import axios from "axios";

const SearchBar = ({ onSearch }) => {
  const [userInput, setUserInput] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  const sendDataToFlask = async () => {
    const apiUrl = 'http://localhost:5000/index';

    const requestData = {
      type: 'custom_type', // You can set a fixed type or make it dynamic as well
      message: userInput,
    };

    try {
      const response = await axios.post(apiUrl, requestData);
      setResponseMessage(response.data.message);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <label>
        Enter Message:
        <input
          name="restaurant"
          type="restaurant"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
        />
      </label>
      <button onClick={sendDataToFlask}>Send POST Request</button>
      <p>Response Message: {responseMessage}</p>
    </div>
  );


  return (
    <>
    {/* <div className="search-containers">
    <div className="search-container">
      <IoSearchOutline style={{position: 'flex', color: "#424242"}} size= "25px"/>
      <input className="search-input" type="text" placeholder="Search..."/> 
    </div>
    <div className="search-container">
    <IoSearchOutline style={{position: 'flex', color: "#424242"}} size= "25px"/>
    <input className="search-input" type="text" placeholder="Search..."/> 
    </div>
    </div> */}
    <div>
      <label>
        Enter Message:
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
        />
      </label>
      <button onClick={sendDataToFlask}>Send POST Request</button>
      <p>Response Message: {responseMessage}</p>
    </div>
    </>
  );
};

export default SearchBar;
