import React, { useEffect, useState } from "react";
import "./Searchbar.css";
import { IoSearchOutline } from "react-icons/io5";
import axios from "axios";

const SearchBar = (props, { onSearch }) => {

  const [restaurant, setInput1] = useState('');
  const [postal, setInput2] = useState('');

  const sendDataToFlask = async () => {
    const apiUrl = 'http://localhost:5000/flask/process_data';

    const requestData = {
      restaurant: restaurant,
      postal: postal,
    };

    try {
      const response = await axios.post(apiUrl, requestData);
      props.setProcessedData(response.data.message);
    } catch (error) {
      console.error('Error:', error);
    }
  };


  return (
    <>
      <div class="new-search">
      <label>
        Restaurant:
        <input
          type="text"
          value={restaurant}
          onChange={(e) => setInput1(e.target.value)}
          placeholder="Restaurant"
          class=""
        />
      </label>
      <label>
        Postal Code:
        <input
          type="text"
          value={postal}
          onChange={(e) => setInput2(e.target.value)}
          placeholder="Postal code"
        />
      </label>
      <button onClick={sendDataToFlask}>Send POST Request</button>
      <p>Processed Data from Flask: {props.processedData}</p>
    </div>
    </>
  );
};

export default SearchBar;
