import React, { useState } from "react";
import "./displaycase.css";
import { useRef } from 'react';

const section1 = useRef(null)
const section2 = useRef(null)
const section3 = useRef(null)

const scrollToSection = (elementRef) => {
window.scrollTo({
  top: elementRef.current.offsetTop,
  behavior: "smooth",
 });
};

const DisplayCase = () => {
    return (
        <div className="flex-container">
            <div className="sidebar">
                <ul className="sideList">
                <li href="." className="heading">GO TO TOP</li>
                <li href="." className="heading">CONTENT</li>
                <li href="." className="sideItem">Positive Reviews</li>
                <li href="." className="sideItem">Negative Reviews</li>
                <li href="." className="sideItem">Unrelated Reviews</li>
                </ul>
            </div>
            <div className="showCase">
                <div className="header">
                    <h1>Restaurant Name</h1>
                    <div className="divider"></div>
                </div>
                <div className="quote-container">
                    <p className="quote">
                    Diners unanimously applaud [Restaurant Name] for its outstanding culinary delights, impeccable service, and inviting ambiance.
                    </p>
                </div>
                <div className="thegood">
                    <h2>Positive Reviews</h2>
                </div>
                <div className="thebad">
                    <h2>Negative Reviews</h2>
                </div>
                <div className="theirrelevant">
                    <h2>Unrelated Reviews</h2>
                </div>
            </div>
        </div>
    )
}


export default DisplayCase
